db.query.string:
"
        film_id: number;
        actor_id: number: key;
        title: string;
        image_meta: object;
        first_name: string;
        last_name: string
        "

"
      film_id: number: key;
      title: string;
      image_meta: object
  "

select film.film_id, title, image_meta
from film_actor join film on film.film_id = film_actor.film_id
where film_actor.actor_id = {{ actor_id }}
